public class Solution { public int countSubstrings(String s, String t){ int res=0; for(int i=0;i<s.length();i++) for(int j=0;j<t.length();j++){ int diff=0; int p1=i,p2=j; while(p1<s.length()&&p2<t.length()&&(diff<=1)){ if(s.charAt(p1++)!=t.charAt(p2++)) diff++; if(diff==1) res++; } } return res; } }