public class Solution { public int[] decrypt(int[] code,int k){ int n=code.length; int[] res=new int[n]; if(k>0){ int sum=0; for(int i=1;i<=k;i++) sum+=code[i%n]; for(int i=0;i<n;i++){ res[i]=sum; sum+=code[(i+1+k)%n]-code[(i+1)%n]; } } else if(k<0){ int sum=0; for(int i=1;i<=-k;i++) sum+=code[(n-i)%n]; for(int i=0;i<n;i++){ res[i]=sum; sum+=code[(i+ n + k)%n]-code[(i + n + k +1)%n]; } } else return new int[n]; return res; } }